import React, { useEffect, useState } from 'react';
import RestaurantList from '../components/RestaurantList';
import MapView from '../components/MapView';
import MetaTags from '../seo/MetaTags';
import config from '../config';

const Home = () => {
    const [restaurants, setRestaurants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchRestaurants = async () => {
            try {
                // Use the full URL with the API base
                const response = await fetch(`${config.API_URL}/restaurants`);
                if (!response.ok) {
                    throw new Error('Failed to fetch restaurants');
                }
                const data = await response.json();
                // Make sure you're handling the API response structure correctly
                setRestaurants(data.data || data);
            } catch (err) {
                console.error('Error fetching data:', err);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchRestaurants();
    }, []);

    return (
        <div>
            <MetaTags title="Local Restaurant Finder" description="Find the best local restaurants near you." />
            <h1>Local Restaurant Finder</h1>
            {loading && <p>Loading restaurants...</p>}
            {error && <p>Error: {error}</p>}
            {/* Pass restaurants data down to child components */}
            <MapView restaurants={restaurants} />
            <RestaurantList restaurants={restaurants} />
        </div>
    );
};

export default Home;
