import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Container, Grid, Typography, Button, Divider, Card, CardContent,
  Avatar, List, ListItem, ListItemIcon, ListItemText, Tabs, Tab, Box,
  Paper, Rating, Chip, IconButton, CircularProgress
} from '@mui/material';
import { 
  FaMapMarkerAlt, FaPhone, FaGlobe, FaClock, FaUtensils, 
  FaDollarSign, FaWineGlassAlt, FaParking, FaWifi, 
  FaArrowLeft, FaShare, FaBookmark, FaRegBookmark
} from 'react-icons/fa';
import { useRestaurantDetails } from '../hooks/useRestaurants';
import MetaTags from '../seo/MetaTags';
import Gallery from '../components/Gallery';
import ReviewList from '../components/ReviewList';
import MenuSection from '../components/MenuSection';
import MapView from '../components/MapView';
import ErrorBoundary from '../components/ErrorBoundary';
import './RestaurantDetail.css';

const RestaurantDetail = () => {
  const { id } = useParams();
  const { data, isLoading, error } = useRestaurantDetails(id);
  const [tabValue, setTabValue] = useState(0);
  const [isSaved, setIsSaved] = useState(false);

  // Handle tab change
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Toggle restaurant bookmark
  const toggleSave = () => {
    setIsSaved(!isSaved);
    // Here you would implement the actual saving to user's profile
  };

  // Share restaurant
  const shareRestaurant = () => {
    if (navigator.share) {
      navigator.share({
        title: data.name,
        text: `Check out ${data.name} on Local Restaurant Finder!`,
        url: window.location.href
      })
      .catch(err => console.error('Could not share', err));
    } else {
      // Fallback - copy to clipboard
      navigator.clipboard.writeText(window.location.href)
        .then(() => alert('Link copied to clipboard!'))
        .catch(err => console.error('Could not copy text', err));
    }
  };

  if (isLoading) {
    return (
      <Container className="loading-container">
        <CircularProgress />
        <Typography>Loading restaurant details...</Typography>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Typography variant="h4" color="error">Error loading restaurant</Typography>
        <Typography>{error.message}</Typography>
        <Button component={Link} to="/" startIcon={<FaArrowLeft />}>
          Back to Restaurant List
        </Button>
      </Container>
    );
  }

  const { 
    name, cuisine, address, phone, website, rating, priceRange,
    images, description, openingHours, location, features,
    reviews, menu, similarRestaurants
  } = data || {};

  return (
    <ErrorBoundary>
      <Container maxWidth="lg" className="restaurant-detail-container">
        <MetaTags 
          title={`${name} | Local Restaurant Finder`}
          description={description}
          ogImage={images?.[0]}
        />

        <div className="back-button-container">
          <Button 
            component={Link} 
            to="/" 
            startIcon={<FaArrowLeft />}
            variant="text"
          >
            Back to Results
          </Button>
        </div>

        <Paper elevation={3} className="restaurant-hero">
          <div className="restaurant-header">
            <div>
              <Typography variant="h4" component="h1">{name}</Typography>
              <div className="restaurant-subheader">
                <Chip icon={<FaUtensils />} label={cuisine} className="cuisine-chip" />
                <Rating value={rating} precision={0.5} readOnly />
                <Typography variant="body2">
                  {Array(priceRange).fill('$').join('')}
                </Typography>
              </div>
            </div>
            <div className="action-buttons">
              <IconButton onClick={toggleSave} aria-label="save restaurant">
                {isSaved ? <FaBookmark /> : <FaRegBookmark />}
              </IconButton>
              <IconButton onClick={shareRestaurant} aria-label="share restaurant">
                <FaShare />
              </IconButton>
            </div>
          </div>

          <ErrorBoundary>
            <Gallery images={images} restaurantName={name} />
          </ErrorBoundary>

          <div className="restaurant-quick-info">
            <div className="info-item">
              <FaMapMarkerAlt />
              <Typography>{address}</Typography>
            </div>
            {phone && (
              <div className="info-item">
                <FaPhone />
                <Typography>{phone}</Typography>
              </div>
            )}
            {website && (
              <div className="info-item">
                <FaGlobe />
                <Typography>
                  <a href={website} target="_blank" rel="noopener noreferrer">
                    Website
                  </a>
                </Typography>
              </div>
            )}
            {openingHours && (
              <div className="info-item">
                <FaClock />
                <Typography>{openingHours}</Typography>
              </div>
            )}
          </div>

          <Divider className="section-divider" />

          <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
            <Tabs 
              value={tabValue} 
              onChange={handleTabChange}
              variant="scrollable"
              scrollButtons="auto"
            >
              <Tab label="Overview" />
              <Tab label="Menu" />
              <Tab label="Reviews" />
              <Tab label="Location" />
            </Tabs>
          </Box>

          <div className="tab-content">
            {tabValue === 0 && (
              <div className="overview-tab">
                <Typography variant="h6">About</Typography>
                <Typography paragraph>{description}</Typography>

                {features && (
                  <>
                    <Typography variant="h6">Features</Typography>
                    <div className="features-grid">
                      {features.includes('alcohol') && (
                        <div className="feature-item">
                          <FaWineGlassAlt />
                          <Typography>Serves Alcohol</Typography>
                        </div>
                      )}
                      {features.includes('parking') && (
                        <div className="feature-item">
                          <FaParking />
                          <Typography>Parking Available</Typography>
                        </div>
                      )}
                      {features.includes('wifi') && (
                        <div className="feature-item">
                          <FaWifi />
                          <Typography>Free WiFi</Typography>
                        </div>
                      )}
                    </div>
                  </>
                )}

                {similarRestaurants && similarRestaurants.length > 0 && (
                  <>
                    <Typography variant="h6">Similar Restaurants</Typography>
                    <div className="similar-restaurants">
                      {similarRestaurants.map(restaurant => (
                        <Card key={restaurant.id} className="similar-restaurant-card">
                          <img 
                            src={restaurant.image || 'https://via.placeholder.com/100x100?text=Restaurant'} 
                            alt={restaurant.name}
                          />
                          <CardContent>
                            <Typography variant="subtitle1">{restaurant.name}</Typography>
                            <Typography variant="body2">{restaurant.cuisine}</Typography>
                            <Button 
                              component={Link} 
                              to={`/restaurant/${restaurant.id}`} 
                              size="small"
                            >
                              View
                            </Button>
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  </>
                )}
              </div>
            )}

            {tabValue === 1 && (
              <div className="menu-tab">
                {menu && menu.length > 0 ? (
                  menu.map((section, index) => (
                    <MenuSection key={index} section={section} />
                  ))
                ) : (
                  <Typography>Menu information is not available.</Typography>
                )}
              </div>
            )}

            {tabValue === 2 && (
              <div className="reviews-tab">
                {reviews && reviews.length > 0 ? (
                  <ReviewList reviews={reviews} restaurantId={id} />
                ) : (
                  <Typography>No reviews yet. Be the first to review!</Typography>
                )}
              </div>
            )}

            {tabValue === 3 && (
              <div className="location-tab">
                <ErrorBoundary>
                  <MapView 
                    restaurants={[data]} 
                    userLocation={null}
                    zoom={15}
                    height={400}
                  />
                </ErrorBoundary>

                <Typography variant="h6" className="directions-header">Get Directions</Typography>
                <div className="directions-links">
                  <Button 
                    variant="outlined"
                    href={`https://www.google.com/maps/dir/?api=1&destination=${encodeURIComponent(address)}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Google Maps
                  </Button>
                  <Button 
                    variant="outlined"
                    href={`https://maps.apple.com/?daddr=${encodeURIComponent(address)}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Apple Maps
                  </Button>
                </div>
              </div>
            )}
          </div>
        </Paper>
      </Container>
    </ErrorBoundary>
  );
};

export default RestaurantDetail;
