import React from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Typography,
  Button,
  Box,
  IconButton,
  Grid,
  Chip,
  Rating,
  Divider
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

const RestaurantDetail = ({ open, restaurant, onClose }) => {
  if (!restaurant) return null;

  const formatPrice = (priceRange) => {
    return Array(priceRange).fill('$').join('');
  };

  return (
    <Dialog 
      open={open} 
      onClose={onClose}
      fullWidth
      maxWidth="md"
      disableEnforceFocus
    >
      <DialogTitle sx={{ m: 0, p: 2 }}>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h6">{restaurant.name}</Typography>
          <IconButton aria-label="close" onClick={onClose}>
            <CloseIcon />
          </IconButton>
        </Box>
      </DialogTitle>
      <Divider />
      <DialogContent>
        <Grid container spacing={2}>
          <Grid item xs={12} md={6}>
            <Box component="img" 
              src={restaurant.image || 'https://via.placeholder.com/600x400?text=No+Image+Available'} 
              alt={restaurant.name}
              sx={{ width: '100%', borderRadius: 1, mb: 2 }}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <Box display="flex" alignItems="center" mb={1}>
              <Typography variant="body2" color="text.secondary" mr={1}>
                {restaurant.cuisine}
              </Typography>
              <Chip 
                size="small" 
                label={formatPrice(restaurant.priceRange)} 
                sx={{ mr: 1 }}
              />
              <Rating value={restaurant.rating} readOnly precision={0.5} size="small" />
            </Box>

            <Typography variant="body2" paragraph>
              {restaurant.description || 'No description available for this restaurant.'}
            </Typography>

            <Typography variant="subtitle2" gutterBottom>Address</Typography>
            <Typography variant="body2" paragraph>
              {restaurant.address || 'Address not available'}
            </Typography>

            <Typography variant="subtitle2" gutterBottom>Contact</Typography>
            <Typography variant="body2">
              {restaurant.phone || 'Phone not available'}
            </Typography>

            {restaurant.website && (
              <Button 
                href={restaurant.website} 
                target="_blank" 
                rel="noopener noreferrer"
                sx={{ mt: 1, p: 0, textTransform: 'none' }}
              >
                Visit Website
              </Button>
            )}
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Close</Button>
        <Button 
          variant="contained" 
          color="primary"
          // You'd implement this in a real app:
          // onClick={() => window.open(`https://maps.google.com/?q=${restaurant.address}`, '_blank')}
        >
          Get Directions
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default RestaurantDetail;
