import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RestaurantCard from './RestaurantCard';
import './RestaurantList.css'; // Assuming you have some CSS for styling
import config from '../config';

const API_URL = config.API_URL;

const RestaurantList = () => {
    const [restaurants, setRestaurants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        let isMounted = true;

        const fetchRestaurants = async () => {
            try {
                // Temporarily hardcode the URL to confirm if that's the issue
                const response = await axios.get('http://localhost:5001/api/restaurants');
                console.log('Response received:', response);
                if (isMounted) {
                    setRestaurants(response.data.data || response.data);
                }
            } catch (err) {
                console.error('Error details:', err);
                if (isMounted) {
                    if (err.response) {
                        // The request was made and the server responded with a status code
                        // that falls out of the range of 2xx
                        console.error('Response data:', err.response.data);
                        console.error('Response status:', err.response.status);
                        console.error('Response headers:', err.response.headers);
                        setError(`Error ${err.response.status}: ${err.message}`);
                    } else if (err.request) {
                        // The request was made but no response was received
                        console.error('Request made but no response received');
                        setError('No response from server. Is it running?');
                    } else {
                        // Something happened in setting up the request that triggered an Error
                        console.error('Error setting up request:', err.message);
                        setError(`Error: ${err.message}`);
                    }
                }
            } finally {
                if (isMounted) {
                    setLoading(false);
                }
            }
        };

        fetchRestaurants();

        // Cleanup function to prevent memory leaks
        return () => {
            isMounted = false;
        };
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="restaurant-list">
            {restaurants.length > 0 ? (
                restaurants.map((restaurant) => (
                    <RestaurantCard 
                        key={restaurant.id.toString()} 
                        restaurant={{
                            ...restaurant,
                            id: restaurant.id.toString(),
                            // Add a default location if it doesn't exist
                            location: restaurant.location || {
                                lat: restaurant.lat || 37.7749,
                                lng: restaurant.lng || -122.4194
                            }
                        }} 
                    />
                ))
            ) : (
                <div>No restaurants found.</div>
            )}
        </div>
    );
};

export default RestaurantList;
