import React, { useState, useEffect, useRef } from 'react';
import { 
  Container, Grid, Typography, Divider, Paper, TextField, Slider, 
  FormControlLabel, Switch, Chip, Box, Button, InputAdornment,
  Dialog, DialogTitle, DialogContent, DialogActions, Rating,
  useMediaQuery, CircularProgress, Alert
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import LocalDiningIcon from '@mui/icons-material/LocalDining';
import axios from 'axios';

import RestaurantList from '../components/RestaurantList';
import MapView from '../components/MapView';
import MetaTags from '../seo/MetaTags';
import RestaurantDetail from '../components/RestaurantDetail'; // Ensure this path is correct
import config from '../config';

const API_URL = config.API_URL;

const cuisineOptions = [
  'Italian', 'Japanese', 'Chinese', 'Mexican', 'Indian', 'Thai', 'American', 'French', 'Mediterranean', 'Korean'
];

const Home = () => {
  // State for restaurants and loading
  const [restaurants, setRestaurants] = useState([]);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State for user location
  const [location, setLocation] = useState(null);

  // State for search and filters
  const [keyword, setKeyword] = useState('');
  const [priceRange, setPriceRange] = useState([1, 4]);
  const [ratingFilter, setRatingFilter] = useState(0);
  const [selectedCuisines, setSelectedCuisines] = useState([]);
  const [openNow, setOpenNow] = useState(false);

  // State for restaurant details modal
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [detailOpen, setDetailOpen] = useState(false);

  // State for filter dialog
  const [filterDialogOpen, setFilterDialogOpen] = useState(false);

  // Ref for filter button
  const filterButtonRef = useRef(null);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Fetch restaurants on mount and when location changes
  useEffect(() => {
    const fetchRestaurants = async () => {
      setLoading(true);
      setError(null);
      try {
        // Get user's location if available
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              const userLocation = {
                lat: position.coords.latitude,
                lng: position.coords.longitude
              };
              setLocation(userLocation);
              fetchNearbyRestaurants(userLocation);
            },
            (error) => {
              console.error('Error getting location:', error);
              fetchNearbyRestaurants();
            }
          );
        } else {
          fetchNearbyRestaurants();
        }
      } catch (err) {
        console.error('Error:', err);
        setError('Failed to load restaurants. Please try again later.');
        setLoading(false);
      }
    };

    const fetchNearbyRestaurants = async (userLoc) => {
      try {
        let url = `${API_URL}/restaurants`;
        let params = {};

        if (userLoc) {
          params.lat = userLoc.lat;
          params.lng = userLoc.lng;
        }

        const response = await axios.get(url, { params });
        const data = response.data.data || response.data;
        setRestaurants(data);
        setFilteredRestaurants(data);
      } catch (err) {
        console.error('Error fetching restaurants:', err);
        setError('Failed to load restaurants. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, []);

  // Apply filters when any filter criteria change
  useEffect(() => {
    if (restaurants.length) {
      let results = [...restaurants];

      // Apply keyword filter
      if (keyword) {
        const searchTerm = keyword.toLowerCase();
        results = results.filter(restaurant => 
          restaurant.name.toLowerCase().includes(searchTerm) || 
          restaurant.cuisine.toLowerCase().includes(searchTerm) ||
          (restaurant.description && restaurant.description.toLowerCase().includes(searchTerm))
        );
      }

      // Apply price range filter
      results = results.filter(restaurant => 
        restaurant.priceRange >= priceRange[0] && restaurant.priceRange <= priceRange[1]
      );

      // Apply rating filter
      if (ratingFilter > 0) {
        results = results.filter(restaurant => restaurant.rating >= ratingFilter);
      }

      // Apply cuisine filter
      if (selectedCuisines.length > 0) {
        results = results.filter(restaurant => 
          selectedCuisines.includes(restaurant.cuisine)
        );
      }

      // Apply open now filter
      if (openNow) {
        // Mock implementation - in a real app, you'd check actual opening hours
        const now = new Date();
        const day = now.toLocaleDateString('en-US', { weekday: 'short' }).toLowerCase();
        const time = now.getHours() * 100 + now.getMinutes();

        results = results.filter(restaurant => {
          // This is a simplified check - in production you'd have real opening hours data
          return true; // Replace with actual check when you have real data
        });
      }

      setFilteredRestaurants(results);
    }
  }, [restaurants, keyword, priceRange, ratingFilter, selectedCuisines, openNow]);

  const handleRestaurantSelect = (restaurant) => {
    setSelectedRestaurant(restaurant);
    setDetailOpen(true);
  };

  const handleCloseDetail = () => {
    setDetailOpen(false);
  };

  const handleCuisineToggle = (cuisine) => {
    if (selectedCuisines.includes(cuisine)) {
      setSelectedCuisines(selectedCuisines.filter(c => c !== cuisine));
    } else {
      setSelectedCuisines([...selectedCuisines, cuisine]);
    }
  };

  const handlePriceRangeChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  const handleRatingChange = (event, newValue) => {
    setRatingFilter(newValue);
  };

  const handleResetFilters = () => {
    setKeyword('');
    setPriceRange([1, 4]);
    setRatingFilter(0);
    setSelectedCuisines([]);
    setOpenNow(false);
    setFilterDialogOpen(false);
  };

  const priceRangeLabels = {
    1: '$',
    2: '$$',
    3: '$$$',
    4: '$$$$'
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <MetaTags 
        title="Local Restaurant Finder" 
        description="Discover the best local restaurants near you. Filter by cuisine, price range, and more."
      />

      <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
        <RestaurantIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
        Local Restaurant Finder
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {/* Search and filter bar */}
      <Paper elevation={2} sx={{ p: 2, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              placeholder="Search restaurants, cuisines, etc."
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={12} md={6} sx={{ display: 'flex', justifyContent: { xs: 'flex-start', md: 'flex-end' } }}>
            <Button
              ref={filterButtonRef}
              variant="outlined"
              startIcon={<FilterListIcon />}
              onClick={() => setFilterDialogOpen(true)}
              sx={{ mr: 1 }}
            >
              Filters
            </Button>
            {selectedCuisines.length > 0 && (
              <Chip 
                label={`${selectedCuisines.length} cuisines`} 
                onDelete={handleResetFilters}
                sx={{ mr: 1 }}
              />
            )}
            {ratingFilter > 0 && (
              <Chip 
                label={`${ratingFilter}+ stars`} 
                onDelete={() => setRatingFilter(0)}
                sx={{ mr: 1 }}
              />
            )}
            {(priceRange[0] > 1 || priceRange[1] < 4) && (
              <Chip 
                label={`${priceRangeLabels[priceRange[0]]} - ${priceRangeLabels[priceRange[1]]}`} 
                onDelete={() => setPriceRange([1, 4])}
              />
            )}
          </Grid>
        </Grid>
      </Paper>

      {/* Main content area */}
      <Grid container spacing={3}>
        {/* Map View */}
        <Grid item xs={12}>
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
              <CircularProgress />
            </Box>
          ) : (
            <MapView 
              restaurants={filteredRestaurants} 
              onRestaurantSelect={handleRestaurantSelect}
            />
          )}
        </Grid>

        {/* Restaurant List */}
        <Grid item xs={12}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h5" component="h2">
              {filteredRestaurants.length} Restaurants
            </Typography>
            {filteredRestaurants.length !== restaurants.length && (
              <Button size="small" color="primary" onClick={handleResetFilters}>
                Clear Filters
              </Button>
            )}
          </Box>
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
              <CircularProgress />
            </Box>
          ) : filteredRestaurants.length > 0 ? (
            <RestaurantList 
              restaurants={filteredRestaurants}
              onRestaurantSelect={handleRestaurantSelect}
            />
          ) : (
            <Paper sx={{ p: 3, textAlign: 'center' }}>
              <Typography variant="body1">
                No restaurants found matching your criteria. Try adjusting your filters.
              </Typography>
            </Paper>
          )}
        </Grid>
      </Grid>

      {/* Filter Dialog */}
      <Dialog 
        open={filterDialogOpen} 
        onClose={() => {
          setFilterDialogOpen(false);
          if (filterButtonRef.current) {
            filterButtonRef.current.focus();
          }
        }}
        fullWidth
        maxWidth="sm"
        fullScreen={isMobile}
        disableEnforceFocus
      >
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <FilterListIcon sx={{ mr: 1 }} />
            Filter Restaurants
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          <Typography gutterBottom>Price Range</Typography>
          <Slider
            value={priceRange}
            onChange={handlePriceRangeChange}
            valueLabelDisplay="auto"
            valueLabelFormat={(value) => priceRangeLabels[value]}
            min={1}
            max={4}
            marks={[
              { value: 1, label: '$' },
              { value: 2, label: '$$' },
              { value: 3, label: '$$$' },
              { value: 4, label: '$$$$' },
            ]}
          />

          <Box sx={{ mt: 3 }}>
            <Typography gutterBottom>Minimum Rating</Typography>
            <Rating
              name="rating-filter"
              value={ratingFilter}
              precision={0.5}
              onChange={handleRatingChange}
            />
          </Box>

          <Box sx={{ mt: 3 }}>
            <Typography gutterBottom>Cuisines</Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mt: 1 }}>
              {cuisineOptions.map((cuisine) => (
                <Chip
                  key={cuisine}
                  label={cuisine}
                  clickable
                  color={selectedCuisines.includes(cuisine) ? "primary" : "default"}
                  onClick={() => handleCuisineToggle(cuisine)}
                  icon={selectedCuisines.includes(cuisine) ? <LocalDiningIcon /> : undefined}
                />
              ))}
            </Box>
          </Box>

          <Box sx={{ mt: 3 }}>
            <FormControlLabel
              control={
                <Switch
                  checked={openNow}
                  onChange={(e) => setOpenNow(e.target.checked)}
                  color="primary"
                />
              }
              label="Open Now"
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleResetFilters} color="inherit">
            Reset All
          </Button>
          <Button onClick={() => setFilterDialogOpen(false)} color="primary">
            Apply Filters
          </Button>
        </DialogActions>
      </Dialog>

      {/* Restaurant Detail Dialog */}
      {selectedRestaurant && (
        <RestaurantDetails
          open={detailOpen}
          restaurant={selectedRestaurant}
          onClose={handleCloseDetail}
        />
      )}
    </Container>
  );
};

export default Home;
