        description: 'Flavorful curries and tandoori specialties.',
        address: '101 Powell St, San Francisco, CA',
        phone: '(415) 555-3456',
        website: 'https://example.com/curry-house',
        priceRange: 2,
        rating: 4.0,
        location: { lat: 37.7851, lng: -122.4071 },
        image: 'https://source.unsplash.com/random/800x600/?curry'
      },
      {
        id: '5',
        name: 'Paris Bistro',
        cuisine: 'French',
        description: 'Classic French cuisine with an extensive wine list.',
        address: '202 Grant Ave, San Francisco, CA',
        phone: '(415) 555-7890',
        website: 'https://example.com/paris-bistro',
        priceRange: 4,
        rating: 4.7,
        location: { lat: 37.7878, lng: -122.4049 },
        image: 'https://source.unsplash.com/random/800x600/?french-cuisine'
      },
      {
        id: '6',
        name: 'Golden Dragon',
        cuisine: 'Chinese',
        description: 'Traditional Chinese dishes from various regions.',
        address: '303 Clay St, San Francisco, CA',
        phone: '(415) 555-2345',
        website: 'https://example.com/golden-dragon',
        priceRange: 2,
        rating: 4.3,
        location: { lat: 37.7956, lng: -122.4003 },
        image: 'https://source.unsplash.com/random/800x600/?chinese-food'
      }
    ];

    return mockRestaurants;
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <MetaTags 
        title="Local Restaurant Finder" 
        description="Discover great local restaurants near you"
      />

      <Typography variant="h4" component="h1" gutterBottom>
        <RestaurantIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
        Local Restaurant Finder
      </Typography>

      {error && (
        <Alert severity="warning" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {/* Search and filter bar */}
      <Paper elevation={2} sx={{ p: 2, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={8}>
            <TextField
              fullWidth
              placeholder="Search restaurants, cuisines, or food..."
              value={keyword}
              onChange={handleSearchChange}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={12} md={4}>
            <Button
              ref={filterButtonRef}
              variant="outlined"
              startIcon={<FilterListIcon />}
              onClick={() => setFilterDialogOpen(true)}
              fullWidth
            >
              Filters
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {/* Filter indicators */}
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
        {selectedCuisines.map(cuisine => (
          <Chip 
            key={cuisine}
            label={cuisine} 
            onDelete={() => handleCuisineToggle(cuisine)}
            color="primary"
            variant="outlined"
            size="small"
          />
        ))}

        {ratingFilter > 0 && (
          <Chip 
            label={`${ratingFilter}+ Stars`} 
            onDelete={() => setRatingFilter(0)}
            color="primary"
            variant="outlined"
            size="small"
          />
        )}

        {(priceRange[0] > 1 || priceRange[1] < 4) && (
          <Chip 
            label={`${'$'.repeat(priceRange[0])} - ${'$'.repeat(priceRange[1])}`} 
            onDelete={() => setPriceRange([1, 4])}
            color="primary"
            variant="outlined"
            size="small"
          />
        )}

        {openNow && (
          <Chip 
            label="Open Now" 
            onDelete={() => setOpenNow(false)}
            color="primary" 
            variant="outlined"
            size="small"
          />
        )}

        {(selectedCuisines.length > 0 || ratingFilter > 0 || 
           priceRange[0] > 1 || priceRange[1] < 4 || openNow) && (
          <Chip 
            label="Clear All" 
            onClick={handleResetFilters}
            color="secondary"
            size="small"
          />
        )}
      </Box>

      {/* Results count */}
      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">
          {loading ? 'Finding restaurants...' : 
            `${filteredRestaurants.length} ${filteredRestaurants.length === 1 ? 'restaurant' : 'restaurants'} found`}
        </Typography>
      </Box>

      {/* Map view */}
      <Box sx={{ mb: 4 }}>
        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <MapView 
            restaurants={filteredRestaurants} 
            onRestaurantSelect={handleRestaurantSelect}
          />
        )}
      </Box>

      {/* Restaurant list */}
      <Box>
        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
            <CircularProgress />
          </Box>
        ) : filteredRestaurants.length > 0 ? (
          <RestaurantList 
            restaurants={filteredRestaurants}
            onRestaurantSelect={handleRestaurantSelect}
          />
        ) : (
          <Paper sx={{ p: 3, textAlign: 'center' }}>
            <Typography variant="h6" gutterBottom>No restaurants found</Typography>
            <Typography>
              Try adjusting your filters or search terms to see more results.
            </Typography>
            <Button 
              variant="contained" 
              color="primary" 
              onClick={handleResetFilters}
              sx={{ mt: 2 }}
            >
              Reset All Filters
            </Button>
          </Paper>
        )}
      </Box>

      {/* Filter dialog */}
      <Dialog 
        open={filterDialogOpen} 
        onClose={() => setFilterDialogOpen(false)}
        fullWidth
        maxWidth="sm"
        disableEnforceFocus
      >
        <DialogTitle>
          Filter Restaurants
        </DialogTitle>
        <DialogContent dividers>
          <Typography gutterBottom>Price Range</Typography>
          <Slider
            value={priceRange}
            onChange={(e, newValue) => setPriceRange(newValue)}
            valueLabelDisplay="auto"
            valueLabelFormat={(value) => '$'.repeat(value)}
            min={1}
            max={4}
            marks={[
              { value: 1, label: '$' },
              { value: 2, label: '$$' },
              { value: 3, label: '$$$' },
              { value: 4, label: '$$$$' },
            ]}
          />

          <Box sx={{ mt: 3 }}>
            <Typography gutterBottom>Minimum Rating</Typography>
            <Rating
              value={ratingFilter}
              onChange={(e, newValue) => setRatingFilter(newValue)}
              precision={0.5}
            />
          </Box>

          <Box sx={{ mt: 3 }}>
            <Typography gutterBottom>Cuisines</Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mt: 1 }}>
              {cuisineOptions.map((cuisine) => (
                <Chip
                  key={cuisine}
                  label={cuisine}
                  onClick={() => handleCuisineToggle(cuisine)}
                  color={selectedCuisines.includes(cuisine) ? "primary" : "default"}
                />
              ))}
            </Box>
          </Box>

          <Box sx={{ mt: 3 }}>
            <FormControlLabel
              control={
                <Switch
                  checked={openNow}
                  onChange={(e) => setOpenNow(e.target.checked)}
                />
              }
              label="Open Now"
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleResetFilters}>
            Reset All
          </Button>
          <Button 
            onClick={() => setFilterDialogOpen(false)} 
            variant="contained" 
            color="primary"
          >
            Apply Filters
          </Button>
        </DialogActions>
      </Dialog>

      {/* Restaurant detail modal */}
      {selectedRestaurant && (
        <RestaurantDetail
          open={detailOpen}
          restaurant={selectedRestaurant}
          onClose={handleCloseDetail}
        />
      )}
    </Container>
  );
};

