import React, { useState, useEffect } from 'react';
import { QueryClient, QueryClientProvider } from 'react-query';
import { ReactQueryDevtools } from 'react-query/devtools';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Container, Grid, CircularProgress, Typography, Snackbar, Alert } from '@mui/material';
import InfiniteScroll from 'react-infinite-scroll-component';
import { AnimatePresence } from 'framer-motion';

import MetaTags from '../seo/MetaTags';
import LocationFinder from '../components/LocationFinder';
import FilterPanel from '../components/FilterPanel';
import MapView from '../components/MapView';
import RestaurantList from '../components/RestaurantList';
import { useRestaurants } from '../hooks/useRestaurants';

// Create a query client
const queryClient = new QueryClient();

// Create a theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#ff4400',
    },
    secondary: {
      main: '#0044ff',
    },
  },
});

const Home = () => {
  const [location, setLocation] = useState({ lat: 37.7749, lng: -122.4194 }); // Default to SF
  const [filters, setFilters] = useState({
    priceRange: [1, 4],
    rating: 0,
    cuisines: [],
    openNow: false,
    keyword: ''
  });
  const [page, setPage] = useState(1);
  const [displayedRestaurants, setDisplayedRestaurants] = useState([]);
  const [hasMore, setHasMore] = useState(true);
  const [mapVisible, setMapVisible] = useState(true);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });

  // Use the custom hook to fetch restaurants
  const { data, isLoading, error } = useRestaurants(location, filters);

  // Update displayed restaurants when data changes
  useEffect(() => {
    if (data?.data) {
      setDisplayedRestaurants(data.data.slice(0, page * 5)); // Show 5 restaurants per "page"
      setHasMore(data.data.length > page * 5);
    }
  }, [data, page]);

  // Reset page when filters or location change
  useEffect(() => {
    setPage(1);
  }, [filters, location]);

  // Handle location change
  const handleLocationChange = (newLocation) => {
    setLocation(newLocation);
    setNotification({
      open: true,
      message: 'Location updated! Finding restaurants nearby...',
      severity: 'success'
    });
  };

  // Handle filter change
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  // Load more restaurants
  const fetchMoreData = () => {
    setPage(prevPage => prevPage + 1);
  };

  // Toggle map visibility
  const toggleMap = () => {
    setMapVisible(!mapVisible);
  };

  // Close notification
  const handleCloseNotification = () => {
    setNotification(prev => ({ ...prev, open: false }));
  };

  return (
    <ThemeProvider theme={theme}>
      <QueryClientProvider client={queryClient}>
        <Container maxWidth="lg">
          <MetaTags 
            title="Local Restaurant Finder" 
            description="Find the best local restaurants near you with filters for cuisine, price, and more." 
          />

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <LocationFinder onLocationChange={handleLocationChange} />
            </Grid>

            <Grid item xs={12}>
              <FilterPanel onFilterChange={handleFilterChange} />
            </Grid>

            {mapVisible && (
              <Grid item xs={12}>
                <MapView 
                  restaurants={data?.data || []} 
                  userLocation={location}
                  isLoading={isLoading}
                />
              </Grid>
            )}

            <Grid item xs={12}>
              {isLoading ? (
                <div className="loading-container">
                  <CircularProgress />
                  <Typography>Finding the best restaurants for you...</Typography>
                </div>
              ) : error ? (
                <Alert severity="error">
                  Error loading restaurants: {error.message}
                </Alert>
              ) : (
                <InfiniteScroll
                  dataLength={displayedRestaurants.length}
                  next={fetchMoreData}
                  hasMore={hasMore}
                  loader={<div className="loading-container"><CircularProgress size={24} /></div>}
                  endMessage={
                    <Typography align="center" color="textSecondary" className="end-message">
                      That's all the restaurants we found in this area!
                    </Typography>
                  }
                >
                  <AnimatePresence>
                    <RestaurantList 
                      restaurants={displayedRestaurants} 
                      userLocation={location}
                    />
                  </AnimatePresence>
                </InfiniteScroll>
              )}
            </Grid>
          </Grid>

          <Snackbar 
            open={notification.open} 
            autoHideDuration={6000} 
            onClose={handleCloseNotification}
          >
            <Alert onClose={handleCloseNotification} severity={notification.severity}>
              {notification.message}
            </Alert>
          </Snackbar>
        </Container>

        {process.env.NODE_ENV === 'development' && <ReactQueryDevtools />}
      </QueryClientProvider>
    </ThemeProvider>
  );
};

export default Home;
