import React, { useState } from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import config from '../config';
import './MapView.css'; // Create this file for styling

const MapView = ({ restaurants = [] }) => {
    const [useGoogleMaps, setUseGoogleMaps] = useState(false);

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: config.GOOGLE_MAPS_API_KEY
    });

    // Function to toggle between placeholder and Google Maps
    const toggleMapView = () => {
        setUseGoogleMaps(!useGoogleMaps);
    };

    return (
        <div className="map-container">
            {useGoogleMaps ? (
                isLoaded ? (
                    <GoogleMap
                        mapContainerStyle={{ width: '100%', height: '300px' }}
                        center={{ lat: 37.7749, lng: -122.4194 }} // Default to San Francisco or another default location
                        zoom={13}
                    >
                        {restaurants.map(restaurant => (
                            <Marker
                                key={restaurant.id}
                                position={restaurant.location}
                                title={restaurant.name}
                            />
                        ))}
                    </GoogleMap>
                ) : (
                    <div>Loading map...</div>
                )
            ) : (
                // Enhanced placeholder with styling
                <div className="map-placeholder">
                    <div className="placeholder-content">
                        <h3>Restaurant Map</h3>
                        <p>{restaurants.length} restaurants found in your area</p>
                        <div className="placeholder-markers">
                            {restaurants.slice(0, 3).map(restaurant => (
                                <div key={restaurant.id} className="placeholder-marker">
                                    <div className="marker-pin"></div>
                                    <div className="marker-info">
                                        <strong>{restaurant.name}</strong>
                                        <span>{restaurant.cuisine}</span>
                                    </div>
                                </div>
                            ))}
                        </div>
                        <button onClick={toggleMapView}>Try Google Maps</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MapView;
