import React from 'react';
import { motion } from 'framer-motion';
import { Card, CardContent, CardMedia, Typography, Rating, Chip, Button } from '@mui/material';
import { FaClock, FaMapMarkerAlt, FaPhoneAlt, FaGlobe, FaUtensils } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import distanceCalculator from '../utils/distanceCalculator';
import './RestaurantCard.css';

const RestaurantCard = ({ restaurant, userLocation, index }) => {
  const {
    id, name, cuisine, address, rating, priceRange,
    image, location, openingHours, phone, website
  } = restaurant;

  // Calculate distance if user location is available
  const distance = userLocation && location 
    ? distanceCalculator(userLocation, location) 
    : null;

  // Format price range
  const formatPriceRange = (range) => {
    return Array(range).fill('$').join('');
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3, delay: index * 0.1 }}
      whileHover={{ scale: 1.02, boxShadow: "0px 5px 15px rgba(0,0,0,0.1)" }}
      className="restaurant-card-container"
    >
      <Card className="restaurant-card">
        <CardMedia
          component="img"
          height="160"
          image={image || 'https://via.placeholder.com/300x160?text=Restaurant'}
          alt={name}
          className="restaurant-image"
        />
        <CardContent className="restaurant-content">
          <div className="restaurant-header">
            <Typography variant="h5" component="h2" className="restaurant-name">
              {name}
            </Typography>
            <div className="restaurant-details">
              <Rating
                name="read-only"
                value={rating}
                precision={0.5}
                readOnly
                size="small"
              />
              <Typography variant="body2" color="textSecondary" component="p">
                {formatPriceRange(priceRange)}
              </Typography>
            </div>
          </div>

          <div className="restaurant-chips">
            <Chip 
              icon={<FaUtensils />} 
              label={cuisine} 
              size="small" 
              variant="outlined" 
              className="cuisine-chip"
            />
            {distance && (
              <Chip 
                icon={<FaMapMarkerAlt />} 
                label={`${distance.toFixed(1)} mi`} 
                size="small" 
                variant="outlined" 
                className="distance-chip"
              />
            )}
          </div>

          <Typography variant="body2" color="textSecondary" component="p" className="restaurant-address">
            <FaMapMarkerAlt /> {address}
          </Typography>

          {openingHours && (
            <Typography variant="body2" color="textSecondary" component="p" className="restaurant-hours">
              <FaClock /> {openingHours}
            </Typography>
          )}

          <div className="restaurant-actions">
            {phone && (
              <Button 
                startIcon={<FaPhoneAlt />}
                href={`tel:${phone}`}
                size="small"
                variant="outlined"
              >
                Call
              </Button>
            )}

            {website && (
              <Button 
                startIcon={<FaGlobe />}
                href={website}
                target="_blank"
                rel="noopener noreferrer"
                size="small"
                variant="outlined"
              >
                Website
              </Button>
            )}

            <Button 
              component={Link}
              to={`/restaurant/${id}`}
              size="small"
              variant="contained"
              color="primary"
            >
              Details
            </Button>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  );
};

export default RestaurantCard;
